INSERT INTO `dsc_article` VALUES ('15', '7', '货到付款区域', '<p>货到付款区域已基本覆盖全国（偏远地区除外）。</p><p>目前仅支持2万以下的订单采用货到付款方式。</p><p>给您发货后，配送人员会送货到您家，当配送人员给您送货的时候，首先您要先签收包裹，然后将货款交给配送人员。再要求配送人员当面验货。确认无误后，方可让配送人员离开！如在验货的过程中遇到了问题，请及时与我们的客服人员联系。</p>', '', '', '', '0', '1', '1242577023', '', '0', 'http://', '', '50');
