INSERT INTO `dsc_article` VALUES ('47', '14', '入驻帮助', '<div class=\"sett-help-warp s-section-help\"><div class=\"item\"><p><strong><span style=\"font-size: 18px;\">01</span></strong><br/></p><div><div><div><strong><span style=\"font-size: 18px;\">在线填写入驻信息，关闭或者退出页面时是否可以自动保存？</span></strong></div><p>商家每次在点击下一步后，系统都会自动保存上一步已经填写的内容，关闭或者退出页面后，下次用同样的用户名和密码登陆，都会自动跳转到上次填写的那一个步骤，上传的资质文件系统会自动保存。</p></div></div></div><div><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">02</span></strong></p><div><div><div><strong><span style=\"font-size: 18px;\">如果注册账号已在朗辰平台开通过店铺，能否使用同一注册账号在卖家入驻系统再次新开店铺？</span></strong></div><p>目前一个注册账号仅支持开通和运营一家店铺。</p><p><br/></p></div></div></div><div><div><div><p><br/></p><p><br/></p></div></div></div></div>', 'ecmoban', 'ecmoban@qq.com', 'ecmoban', '0', '1', '1399151063', '', '0', 'http://', '', '50');
