INSERT INTO `dsc_article` VALUES ('67', '19', '发票类型', '<p><span style=\"font-size: 18px;\"><strong>1、普通发票</strong></span></p><p>一般情况下，普通发票用于平台内商家与消费者的交易行为，商家有义务给消费者开具符合订单金额的发票；</p><p>默认开具电子发票，如需纸质，请联系商家说明。</p><p><br/></p><p><span style=\"font-size: 18px;\"><strong>2、增值税专用发票</strong></span></p><p>增值税专用发票针对商家与平台之间发生的交易行为，包括但不限于系统维护费用、服务费用、交易扣点等。</p><p><br/></p>', '', '', '', '0', '1', '1538936141', '', '0', 'http://', '', '50');
